// OutputGroupGroupBy is the group-by builder for OutputGroup entities.
type OutputGroupGroupBy struct {
	selector
	build *OutputGroupQuery
}

// Aggregate adds the given aggregation functions to the group-by query.
func (oggb *OutputGroupGroupBy) Aggregate(fns ...AggregateFunc) *OutputGroupGroupBy {
	oggb.fns = append(oggb.fns, fns...)
	return oggb
}

// Scan applies the selector query and scans the result into the given value.
func (oggb *OutputGroupGroupBy) Scan(ctx context.Context, v any) error {
	ctx = setContextOp(ctx, oggb.build.ctx, "GroupBy")
	if err := oggb.build.prepareQuery(ctx); err != nil {
		return err
	}
	return scanWithInterceptors[*OutputGroupQuery, *OutputGroupGroupBy](ctx, oggb.build, oggb, oggb.build.inters, v)
}

func (oggb *OutputGroupGroupBy) sqlScan(ctx context.Context, root *OutputGroupQuery, v any) error {
	selector := root.sqlQuery(ctx).Select()
	aggregation := make([]string, 0, len(oggb.fns))
	for _, fn := range oggb.fns {
		aggregation = append(aggregation, fn(selector))
	}
	if len(selector.SelectedColumns()) == 0 {
		columns := make([]string, 0, len(*oggb.flds)+len(oggb.fns))
		for _, f := range *oggb.flds {
			columns = append(columns, selector.C(f))
		}
		columns = append(columns, aggregation...)
		selector.Select(columns...)
	}
	selector.GroupBy(selector.Columns(*oggb.flds...)...)
	if err := selector.Err(); err != nil {
		return err
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := oggb.build.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

